// JavaScript code for fetching data from the API and populating the table

const url = 'https://commodity-rates-api.p.rapidapi.com/latest?base=USD&symbols=BRENTOIL';
const options = {
    method: 'GET',
    headers: {
        'X-RapidAPI-Key': 'eac5a915fdmshcbdb59707a098c3p193cf1jsne5708539a314',
        'X-RapidAPI-Host': 'commodity-rates-api.p.rapidapi.com'
    }
};

try {
    const response = await fetch(url, options);
    const data = await response.json();

    const tableBody = document.getElementById('pricingTable');
    const row = document.createElement('tr');
    row.innerHTML = `
        <td>Brent Oil</td>
        <td>${data.rates.BRENTOIL}</td>
    `;
    tableBody.appendChild(row);
} catch (error) {
    console.error(error);
}







